import React, { createContext, useReducer, useContext, useEffect } from 'react';
import { makeAutoObservable } from "mobx";

class CartStore {
  cart = []; // Tableau pour stocker les articles du panier

  constructor() {
    makeAutoObservable(this);
  }

  addToCart(article) {
      // Vérifiez d'abord si l'article est déjà dans le panier
      const existingCartItem = this.cart.find((item) => item.id === article.id);

      if (existingCartItem) {
        // Si l'article existe déjà dans le panier, on augmente sa quantité
        existingCartItem.quantity += 1;
      } else {
        // Sinon, ajoutez l'article au panier avec une quantité de 1
        this.cart.push({ ...article, quantity: 1 });
      }

    this.cart.push(product);
  }

  removeFromCart(productId) {
    // Votre logique pour retirer un produit du panier
    // Par exemple, vous pouvez utiliser filter pour supprimer l'article
    this.cart = this.cart.filter(item => item.productId !== productId);
  }

  getQuantity(productId) {
    // Votre logique pour obtenir la quantité d'un produit dans le panier
    // Par exemple, vous pouvez utiliser reduce pour compter les occurrences
    return this.cart.reduce((count, item) => {
      if (item.productId === productId) {
        return count + 1;
      }
      return count;
    }, 0);
  }

  // D'autres méthodes ou propriétés du panier si nécessaire
}

const cartStore = new CartStore();

export default cartStore;
