import React, { useContext } from "react";
import { GlobalContext } from '../App.jsx';
import Article from '../components/Article';
import QuantityButton from "./QuantityButton";
import Bar from "./Bar.jsx";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { far } from '@fortawesome/free-regular-svg-icons'; // Importez les icônes régulières

library.add(far);


function SectionArticle({ articles, sectionName, sectionIcon, alignLeft = true }) {

    const icon = sectionIcon ? far[sectionIcon] : null;

    return (
        <section>
            <div className="w-full h-10 flex justify-center items-center">
                <div className=" w-11/12 h-full bg-dark-color flex items-center">
                    {alignLeft === true ?
                        <>  {sectionIcon && (
                            <FontAwesomeIcon
                                icon={icon}
                                className={`text-light-color`}
                            />
                            )}
                            <p className="text-light-color">{sectionName}</p>
                        </> :
                        <> <p className="text-light-color">{sectionName}</p>
                            {sectionIcon && (
                                <FontAwesomeIcon
                                    icon={icon}
                                    className={`text-light-color`}
                                />
                            )}
                        </>
                    }

                </div>
            </div>
            <Bar width="w-4/5" height="h-1" className="w-full" />
            <div className='flex flex-wrap justify-center'>
                {articles.map(article => (
                    <Article key={article.id} article={article} bar={false} />
                ))}
            </div>
            <Bar width="w-4/5" height="h-1" className="w-full m-2" />
        </section>
    )
}

export default SectionArticle;
