import { GlobalContext } from '../App.jsx';
import { observer } from "mobx-react-lite";
import React, { useContext, useState } from 'react';
import NavItem from '../components/Navitem.jsx';
import NavPaiement from '../components/NavPaiement.jsx';
import PaiementCard from '../components/PaiementCard.jsx';

function Paiement() {

    const { cartStore } = useContext(GlobalContext);

    const clearStorage = () => {
        cartStore.clearLocalStorage();
    };


    return (
        <main className='paiement-section'>
            <NavPaiement/>
            <div>
                <PaiementCard/>
            </div>
        </main> 
    )
}
export default observer(Paiement);
