import React, { useContext } from 'react';
import NavItem from './Navitem';
import { useLocation } from 'react-router-dom';
import { GlobalContext } from '../App.jsx';
import { useNavigate } from 'react-router-dom';


function Nav() {


    const location = useLocation();

    const { currentPageStore } = useContext(GlobalContext);

    let page = currentPageStore.currentPage;

    const navigate = useNavigate();

    const goToExtras = () => {
        if (page != null) {
            page = 'expositions';
        };
        navigate('/Expositions');
    };

    const goToExpositions = () => {
        if (page != null) {
            page = 'extras';
        };
        navigate('/Extras');
    };


    return (
        <nav className='w-full flex justify-between items-center px-10'>
            <img className='w-52' src="../../Noir_Logo_Musee_Lexplorateur.svg" alt="Logo du musÃ©e l'explorateur" />
            <ul className='h-16 w-60 space-y-4'>
                {page === 'expositions' || location.pathname !== '/expositions' && (
                    <NavItem name="Expositions" link="/expositions" onClick={goToExpositions} />
                )}
                {page === 'extras' ||  location.pathname !== '/extras' && (
                    <NavItem name="Extras" link="/extras" onClick={goToExtras} />
                )}
            </ul>
        </nav>
    );
}



export default Nav;
