import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Importez useNavigate depuis react-router-dom
import { GlobalContext } from '../App.jsx';
import NavPaiement from '../components/NavPaiement.jsx';
import PaiementCard from '../components/PaiementCard.jsx';
import InstructionBanner from '../components/InstructionBanner.jsx';
import ArianeThread from '../components/ArianeThread.jsx';

function GiftCard() {
    const navigate = useNavigate(); // Initialisez la fonction navigate pour effectuer la redirection
    const [redirect, setRedirect] = useState(false);

    useEffect(() => {
        // Délai de 3 secondes avant de déclencher la redirection
        const timer = setTimeout(() => {
            setRedirect(true);
        }, 3000);

        return () => clearTimeout(timer);
    }, []);

    if (redirect) {
        navigate('/PaiementStatus', {
            state: {
                success: true,
                textSuccess: 'Carte cadeau acceptée'
            }
        });
    }

    return (
        <main className='paiement-section lg:justify-between'>
            <ArianeThread step={1} />
            <NavPaiement />
            <div className='flex justify-center w-full h-full sm:mt-12'>
                {/* Le contenu de votre page */}
            </div>
        </main>
    );
}

export default GiftCard;
