import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite'; // Importez observer
import { GlobalContext } from '../App';

import NavItem from './Navitem';
import Bar from "./Bar";

// Utilisez observer pour que le composant réagisse aux changements d'état
const Panier = observer(() => {
  const { cartStore } = useContext(GlobalContext);
  const cartArticles = cartStore.cart; // Récupérer les articles du panier depuis le store

  return (
    <div className="border-dashed border-2 border-sky-500">
      <div className="flex">
        <div>Ma commande</div>
        <ul>
          {cartArticles.map((item) => (
            <li key={item.id}>
              {item.name} - Quantité : {item.quantity}
            </li>
          ))}
        </ul>
      </div>
      <div>
        <NavItem name="Abandonner ma commande" link="/expositions" />
        <NavItem name="Confirmer ma commande" link="/expositions" />
      </div>
    </div>
  );
});

export default Panier;
