import React, { useContext, useState } from "react";
import { GlobalContext } from '../App.jsx';
import QuantityButton from "./QuantityButton";
import Bar from "./Bar.jsx";
import ArticleModal from "./ArticleModal.jsx";


function Article({ article, popup = false, bar = true }) {

    const [showModal, setShowModal] = useState(false);
    const [selectedArticle, setSelectedArticle] = useState(null);

    const openModal = (article) => {
        setSelectedArticle(article);
        setShowModal(true);
    };

    const closeModal = () => {
        setSelectedArticle(null);
        setShowModal(false);
    };

    return (
        <article className={`article-container ${bar ? 'with-bar' : ''}`}>
            <div className="article-content">
                <div className="image-container">
                    <img
                        className="article-image"
                        src={`../../pictures/${article.pictures[0]}`}
                        alt={article.alt}
                        onClick={() => openModal(article)}
                    />
                    <div
                        className="overlay"
                        onClick={() => openModal(article)}
                    >
                        <img src="../../index_picto.svg" alt="Cliquez ici" className="w-2/3 h-2/3 hand-tap" />
                    </div>
                </div>
                <div className="article-details">
                    <p className="article-name">{article.name}</p>
                    <div className="article-price">
                        <p className="text-2xl bg-primary-color text-light-color p-3 w-9/12">{article.price}$</p>
                    </div>
                </div>
            </div>
            {showModal && selectedArticle && (
                <ArticleModal article={selectedArticle} onClose={closeModal} />
            )}
        </article>
    )
}

export default Article;
