import Article from '../components/Article';
import React, { useContext } from 'react';
import Nav from '../components/Nav'
import { GlobalContext } from '../App.jsx';
import { observer } from 'mobx-react-lite';
import Panier from '../components/Panier';
import Button from './Button';

function Expositions() {

    const { articleStore } = useContext(GlobalContext);
    const { cartStore } = useContext(GlobalContext);

    const [showModal, setShowModal] = useState(false);

    const handleDeleteClick = () => {
        setShowModal(true);
    };

    const handleModalConfirm = () => {
        setShowModal(false);
    };

    const handleModalCancel = () => {
        setShowModal(false);
    };


    // On acccède aux données des articles via ArticleStore
    const articles = articleStore.articles;

    // Filtrer les articles qui correspondent aux expositions
    const expositionArticles = articles.filter(article => article.id.slice(0, 3) === 'EXP');


    return (
        <>
            {showModal && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
                    <div className="bg-light-color p-4 rounded shadow h-48 w-1/2 flex flex-col justify-evenly">
                        <p className='text-center text-xl'>Souhaitez vous vraiment abandonner votre commande ?</p>
                        <div className="flex justify-evenly mt-4 h-2/5">
                        </div>
                    </div>
                </div>
            )}
        </>
    )
}
export default observer(Expositions);
