    return (

        <div className="border-dashed border-2 border-sky-500">
            <div className="flex">
                <div>Ma commande</div>
                <ul>
        {cartStore.cart.map((item) => (
          <li key={item.id}>
            {item.name} - Quantit√© : {item.quantity}
