import { makeAutoObservable} from "mobx";


class CartStore {
    _cart; // Tableau pour stocker les articles du panier

    constructor() {
        _cart = localStorage.getItem("basket");
        if (_cart == null) {
            this._cart = [];
        } else {
           this._cart = JSON.parse(_cart);
        }
        
        makeAutoObservable(this);
    }

    saveCart ()  {
        localStorage.setItem("basket", JSON.stringify(this.cart)); 
    }

    addCart (article) {
        let cart = getCart();
        let foundArticle = this.cart.find (item => item.id == article.id );
        if (foundArticle != undefined) {
            foundArticle.quantity++;
        } else {
            product.quantity = 1;
            cart.push(article);
        }
        saveCart();
    }

    removeFromCart (article) {
        this.cart = this.cart.filter(item => item.id != article.id);
        saveCart();
    }

    changeQuantity(article, quantity) {
        let foundArticle = this.cart.find (item => item.id == article.id );
        if (foundArticle != undefined) {
            foundArticle.quantity+= quantity;
            if (foundArticle.quantity <= 0) {
                removeFromCart(foundArticle);
            } else {
                saveCart();
            }
        }
    }

    getNumberArticle() {
        let number = 0;
        for (let item of this.cart) {
            number += item.quantity;
        }
        return number;
    }

    getTotalPrice = () => {
        let cart = getCart();
        let total = 0;
        for (let item of cart) {
            total += item.quantity * item.price;
        }
        return total;
    }


    get cart() { return this._cart; }

    set cart(cart) { this._cart = cart; }

}

export default CartStore;
