import React, { useContext, useEffect, useState } from 'react';
import { GlobalContext } from '../App';

import NavItem from './Navitem';
import Bar from "./Bar";

function Panier() {

    const { cartStore } = useContext(GlobalContext);
    const [cartArticles, setCartArticles] = useState(cartStore.cart);

      // Utilisez useEffect pour mettre à jour le panier en fonction des changements
  useEffect(() => {
    // Fonction pour mettre à jour le panier lorsque des changements surviennent
    const updateCart = () => {
      setCartArticles([...cartStore.cart]); // Créez une copie du panier pour déclencher une mise à jour
    };

    // Abonnez-vous aux changements dans le panier
    const cartUpdateSubscription = cartStore.subscribeToUpdates(updateCart);

    // Nettoyez l'abonnement lorsque le composant est démonté
    return () => {
      cartUpdateSubscription();
    };
  }, [cartStore]);

    return (

        <div className="border-dashed border-2 border-sky-500">
            <div className="flex">
                <div>Ma commande</div>
                <ul>
                    {cartArticles.map((item) => (
                        <li key={item.id}>
                            {item.name} - Quantité : {item.quantity}
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <NavItem name="Abandonner ma commande" link="/expositions" />
                <NavItem name="Confirmer ma commande" link="/expositions" />
            </div>
        </div>

    )
}

export default Panier;
