import React, { useContext, useEffect, useState } from "react";
import Button from "./Button";
import { GlobalContext } from '../App.jsx';
import { observer } from "mobx-react-lite";

function QuantityButton({ row = true, className, article }) {
    const { cartStore } = useContext(GlobalContext);
    const direction = row ? "row" : "col";

    // Utilisez useState pour gérer la quantité en local dans le composant
    const [quantityInCart, setQuantityInCart] = useState(0);

    useEffect(() => {
        // Chargez la quantité initiale depuis le localStorage lors du montage du composant
        const initialQuantity = cartStore.getQuantityForArticle(article);
        setQuantityInCart(initialQuantity);

        // Ajoutez un écouteur d'événement pour surveiller les changements dans le cartStore
        const updateQuantity = () => {
            const updatedQuantity = cartStore.getQuantityForArticle(article);
            setQuantityInCart(updatedQuantity);
        };

        // Inscrivez le composant comme un observateur des changements du cartStore
        const disposer = cartStore.subscribe(updateQuantity);

        // Retirez l'observateur lors du démontage du composant
        return () => {
            disposer();
        };
    }, [cartStore, article]);

    const handleAddToCart = () => {
        cartStore.addCart(article);
    };

    const handleRemoveFromCart = () => {
        cartStore.removeFromCart(article);
    };

    return (
        <div className={`flex flex-${direction} justify-between items-center w-2/12 ${className && className}`}>
            {row === false ? <Button title="Ajouter" icon="faPlus" onClick={handleAddToCart} /> : <Button title="Retirer" icon="faMinus" onClick={handleRemoveFromCart} />}
            <p className="text-2xl p-3">{quantityInCart}</p>
            {row === false ? <Button title="Retirer" icon="faMinus" onClick={handleRemoveFromCart} /> : <Button title="Ajouter" icon="faPlus" onClick={handleAddToCart} />}
        </div>
    );
}

export default observer(QuantityButton);
