import React, { useContext, useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom'; // Importez Redirect depuis react-router-dom
import { GlobalContext } from '../App.jsx';
import NavPaiement from '../components/NavPaiement.jsx';
import PaiementCard from '../components/PaiementCard.jsx';
import InstructionBanner from '../components/InstructionBanner.jsx';
import ArianeThread from '../components/ArianeThread.jsx';

function GiftCard() {
    const [redirect, setRedirect] = useState(false);

    useEffect(() => {
        // Délai de 3 secondes avant de déclencher la redirection pour la simulation
        const timer = setTimeout(() => {
            setRedirect(true);
        }, 3000);

        return () => clearTimeout(timer);
    }, []);

    if (redirect) {
        return <Redirect to={{
            pathname: '/PaiementStatus',
            state: {
                success: true,
                textSuccess: 'Carte cadeau acceptée'
            }
        }} />;
    }

    return (
        <main className='paiement-section lg:justify-between'>
            <ArianeThread step={1} />
            <NavPaiement />
            <div className='flex justify-center w-full h-full sm:mt-12'>
                {/* Le contenu de votre page */}
            </div>
        </main>
    );
}

export default GiftCard;
