import React, { useContext } from 'react';
import NavItem from './Navitem';
import { GlobalContext } from '../App.jsx';
import { observer } from "mobx-react-lite";

function Panier() {
  const { cartStore } = useContext(GlobalContext);

  const renderCartItem = (article) => {
    const { nom, quantite, prix } = article;
    const montantTotalArticle = quantite * prix;

    return (
      <div key={article.id} className="flex justify-between items-center border-b-2 p-2">
        <div>
          <p>{nom}</p>
          <p>Quantit√©: {quantite}</p>
        </div>
        <div>
          <p>Montant Total: ${montantTotalArticle.toFixed(2)}</p>
        </div>
      </div>
    );
  };

  const montantTotalPanier = cartStore.getTotalPrice();

  return (
    <div className="border-dashed border-2 border-sky-500">
      <div className="flex justify-between items-center p-2">
        <div>Mon panier</div>
        <div>Montant Total du Panier: ${montantTotalPanier.toFixed(2)}</div>
      </div>
      {cartStore.cart.map(renderCartItem)}
      <div className="flex justify-between p-2">
        <NavItem name="Abandonner ma commande" link="/expositions" />
        <NavItem name="Confirmer ma commande" link="/expositions" />
      </div>
    </div>
  );
}

export default observer(Panier);
