import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { far } from '@fortawesome/free-regular-svg-icons';
import { Link } from "react-router-dom";

library.add(far);

function PaiementCard({icon, link, text, height = "h-ful"}) {

    return (
        <Link className={`flex flex-col ${!icon ? 'justify-center' : 'justify-between'} ${height} py-12 px-12 bg-light-color-lightened`} to={link}>
            {icon &&  <FontAwesomeIcon icon={icon} className="w-2/5" />}
            <h2 className="text-5xl italic text-center">{text}</h2>
        </Link>
  
    )
}

export default PaiementCard;
