import { makeAutoObservable} from "mobx";


class CartStore {
    _cart; // Tableau pour stocker les articles du panier

    constructor() {
        this._cart = [];
        makeAutoObservable(this);
    }

    get cart() { return this._cart; }

    set cart(cart) { this._cart = cart; }

    addToCart(article) {
        const existingItem = this.cart.find((item) => item.id === article.id);
        if (existingItem) {
            // Si l'article existe déjà dans le panier, on augmente sa quantité
            existingItem.quantity += 1;
        } else {
            // Sinon on l'ajoute avec une quantité de 1
            this.cart.push({ ...article, quantity: 1 });
        }
    }


    removeFromCart(article) {

        const existingItem = this.cart.find((item) => item.id === article.id);

        if (existingItem) {
            if (existingItem.quantity > 1) {
              // Si la quantité est supérieure à 1, décrémente la quantité
              existingItem.quantity -= 1;
            } else {
              // Sinon, retire complètement l'article du panier
              this.cart.remove(existingItem);
            }
          }
    }

    updateCartItemQuantity(article, newQuantity) {
        const currentArticle = this.existInCart(article.id);
    
        if (currentArticle) {
          // Mettre à jour la quantité de l'article dans le panier
          currentArticle.quantity = newQuantity;
        }
      }

    existInCart(articleId) {
        // Vérifiez si l'article est déjà dans le panier
        const currentArticle = this.cart.find((item) => item.id === articleId);
        if (currentArticle) {
            return currentArticle;
        } else {
            return false;
        }
    }

    getQuantity(productId) {
        const currentArticle = this.existInCart(productId);
        if (currentArticle) {
            return currentArticle.quantity;
          }
        
          // Si l'article n'est pas trouvé, on retourne 0
          return 0;
    }

    getTotalItems() {
        return this.cart.reduce((total, item) => total + item.quantity, 0);
    }
}

export default CartStore;
