import React, { useContext } from 'react';
import NavItem from './Navitem';
import { GlobalContext } from '../App.jsx';
import { observer } from "mobx-react-lite";
import Bar from "./Bar";
import QuantityButton from './QuantityButton';

function Panier() {

    const { cartStore } = useContext(GlobalContext);

    //  {cart.map(article => ( <p>{article.name}</p> ))}

    const clearStorage = () => {
        cartStore.clearLocalStorage();
    };

    return (

        <div className="w-11/12 border-custom sticky flex flex-col bg-light-color-lightened h-50 bottom-0">
            <div className="flex justify-center m-4 basis-1/4 items-stretch justify-evenly">
                <div className='flex items-center'>
                    <div className='h-3/5 border-r-2 border-dark-color text-2xl pr-4 flex items-center'>
                        <h2 className=''>Ma commande</h2>
                    </div>
                </div>

                <ul className='cart-classes bg-light-color-dark shadow-inner-shadow basis-2/3 flex flex-wrap p-4 my-5'>
                    {cartStore.cart.map(article => (
                        <li key={article.id} className='w-full border-b-2 border-primary-color pb-1 mb-1 flex items-center justify-between'>
                            <p className='text-standard-size'>Nom de l'article : {article.name}</p>
                            <div className='flex items-center justify-between w-2/5'>
                                <QuantityButton article={article} small={true} />
                                <Bar row={false} height="h-12" />
                                <p>Quantité : {article.quantity}</p>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            <Bar />
            <p className='text-2xl w-full m-5 text-center'>Total du panier : {cartStore.getTotalPrice()} €</p>
            <div>
                <NavItem name="Abandonner ma commande" link="/expositions" />
                <NavItem name="Confirmer ma commande" link="/expositions" />
            </div>

            <button onClick={clearStorage} className='bg-sky-500'>Vider le panier</button>


            <div className=" w-11/12 flex flex-wrap sticky start-11 bottom-0 border-2 p-4 border-dark-color bg-light-color">
                {cartStore.cart.map(article => (

                    <div className='basis-2/3 flex flex-wrap border-1 p-2 my-5 mb-8 border-gray-400 shadow-button-shadow'>
                        <p className=''>Total du panier : {cartStore.getTotalPrice()} €</p>
                    </div>
                ))}
                <div className='basis-full flex justify-between'>
                    <NavItem className='' name="Abandonner ma commande" link="/expositions" />
                    <NavItem className='' name="Confirmer ma commande" link="/expositions" />
                </div>
                <button onClick={clearStorage} className='bg-sky-500'>Vider le panier</button>
            </div>
        </div>
    )
}

export default observer(Panier);
