import { makeAutoObservable} from "mobx";


class CartStore {
    _cart; // Tableau pour stocker les articles du panier

    constructor() {
        this._cart = [];
        makeAutoObservable(this);
    }

    get cart() { return this._cart; }

    set cart(cart) { this._cart = cart; }

    addCart = (article) => {
        let cart = getCart();
        let foundArticle = cart.find (item => item.id == article.id );
        if (foundArticle != undefined) {
            foundArticle.quantity++;
        } else {
            product.quantity = 1;
        }
        cart.push(article);
        saveCart(cart);
    }

    removeFromCart = (article) => {
        let cart = getCart();
        cart = cart.filter(item => item.id != article.id);
        saveCart(cart);
    }

    changeQuantity = (article, quantity) => {
        let cart = getCart();
        let foundArticle = cart.find (item => item.id == article.id );
        if (foundArticle != undefined) {
            foundArticle.quantity+= quantity;
            if (foundArticle.quantity <= 0) {
                removeFromCart(foundArticle);
            } else {
                saveCart(cart);
            }
        }
    }

    saveCart = (cart) =>  {
        localStorage.setItem("basket", JSON.stringify(cart)); 
    }

    getCart = () =>  {
        let cart = localStorage.getItem("basket");
        if (cart == null) {
            return [];
        } else {
            return JSON.parse(cart);
        }
    }

    getNumberArticle = () => {
        let cart = getCart();
        let number = 0;
        for (let item of cart) {
            number += item.quantity;
        }
        return number;
    }

     getTotalPrice = () => {
        let cart = getCart();
        let total = 0;
        for (let item of cart) {
            total += item.quantity * item.price;
        }
        return total;
    }


}

export default CartStore;
