import React, { useContext } from "react";
import { GlobalContext } from '../App.jsx';
import QuantityButton from "./QuantityButton";


function Article({ article }) {



    return (
        <article className="relative flex justify-between w-2/5 border-dashed m-4 border-2 border-sky-500 items-center">
            <div className="flex flex-col">
                <p className="absolute bottom-0 left-0">{article.name}</p>
                <p className="absolute bottom-0 right-0">{article.price}$</p>
                <div>
                    <figure>
                    {article.pictures.map(picture => (
                        <Article key={article.id} article={article} />
                    ))}
                        <img key={article.id} className="rounded" src={`../../pictures/${article.pictures[0]}`} alt={article.alt} />
                    </figure>
                </div>
            </div>

            <QuantityButton row={false} />


        </article>



    )
}

export default Article;
