import React, {
    createContext,
    useReducer,
    useContext,
    useEffect
} from 'react';
import {
    makeAutoObservable
} from "mobx";

class CartStore {
    cart = []; // Tableau pour stocker les articles du panier

    constructor() {
        makeAutoObservable(this);
    }

    addToCart(article) {
        const currentArticle = this.existInCart(article.id);

        if (currentArticle) {
            // Si l'article existe déjà dans le panier, on augmente sa quantité
            currentArticle.quantity += 1;
        } else {
            // Sinon on l'ajoute avec une quantité de 1
            this.cart.push({
                ...article,
                quantity: 1
            });
        }
    }


    removeFromCart(article) {

        const currentArticle = this.existInCart(article.id);

        if (currentArticle) {
            // Si l'article existe déjà dans le panier, on diminue sa quantité
            if (currentArticle.quantity > 1) {
                // Si la quantité est supérieure à 1, on diminue sa quantité
                currentArticle.quantity -= 1;
              } else {
                // Sinon, on le retire complètement du panier
                this.cart = this.cart.filter((item) => item.id !== article.id);
              }
              
            //currentArticle.quantity -= 1;
            //const index = this.cart.findIndex((item) => item.id === article.id);
           // this.cart.splice(index, 1);

           //this.cart = this.cart.filter((item) => item.id !== article.id);
        }
    }

    updateCartItemQuantity(article, newQuantity) {
        const currentArticle = this.existInCart(article.id);
    
        if (currentArticle) {
          // Mettre à jour la quantité de l'article dans le panier
          currentArticle.quantity = newQuantity;
        }
      }

    existInCart(articleId) {
        // Vérifiez si l'article est déjà dans le panier
        const currentArticle = this.cart.find((item) => item.id === articleId);
        if (currentArticle) {
            return currentArticle;
        } else {
            return false;
        }
    }

    getQuantity(productId) {
        const currentArticle = this.existInCart(productId);
        if (currentArticle) {
            return currentArticle.quantity;
          }
        
          // Si l'article n'est pas trouvé, on retourne 0
          return 0;
    }
}

export default CartStore;
