import React, { useState, useContext } from "react";
import Button from "./Button";
import { GlobalContext } from '../App.jsx';
import { observer } from "mobx-react-lite";

function QuantityButton({ row = true, className, article }) {

    const { cartStore } = useContext(GlobalContext);
    const quantityInCart = article ? cartStore.getQuantity(article.id) : 0;
    const [quantity, setQuantity] = useState(quantityInCart);


    const direction = row ? "row" : "col";

    const handleAddToCart = (article) => {
        cartStore.addToCart(article);
        setQuantity(quantity + 1);
    };

    const handleRemoveFromCart = (article) => {
        if (quantity > 0) {
            setQuantity(quantity - 1);
            cartStore.removeFromCart(article);
        }
    };

    return (

        <div className={`flex flex-${direction} justify-between items-center w-2/12 ${className && className}`}>
            {row === false ? <Button title="Ajouter" icon="faPlus" onClick={() => handleAddToCart(article)} /> : <Button title="Retirer" icon="faMinus" onClick={() => handleRemoveFromCart(article)} />}
            <p className="text-2xl p-3">{quantity && quantity} ou {quantityInCart && quantityInCart}</p>
            {row === false ? <Button title="Retirer" icon="faMinus" onClick={() => handleRemoveFromCart(article)} /> : <Button title="Ajouter" icon="faPlus" onClick={() => handleAddToCart(article)} />}
        </div>

    )
}

export default observer(QuantityButton);
