import Article from '../components/Article';
import React, { useContext , useState} from 'react';
import Nav from '../components/Nav'
import { GlobalContext } from '../App.jsx';
import { observer } from 'mobx-react-lite';
import Panier from '../components/Panier';
import Button from './Button';

function ArticleModal({ article, onClose }) {

  // Quitter la pop en cliquant en dehors
  const handleOutsideClick = (e) => {
    
    if (e.target.classList.contains('popup-background')) {
      // Si c'est le cas, fermez la popup en appelant la fonction onClose
      onClose();
    }
  };

    return (
      <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
        <div className="bg-white w-4/5 h-4/5 rounded-lg shadow-lg">
          <div className="flex justify-end pr-4 pt-4">
            <button
              className="text-gray-600 hover:text-gray-800"
              onClick={onClose}
            >
              X
            </button>
          </div>
          <div className="p-4">
            <h2 className="text-2xl font-semibold mb-2">{article.name}</h2>
            <p className="text-gray-700">{article.description}</p>
            <p className="text-lg font-semibold mt-4">${article.price}</p>
            {/* Ajoutez ici d'autres d√©tails de l'article que vous souhaitez afficher */}
          </div>
        </div>
      </div>
    );
  }
  
  export default ArticleModal;
