import {
    makeAutoObservable,
    runInAction
} from "mobx";
import Article from "./Article";

class ArticleStore {


    // Attributs
    _articles;

    // Constructeur
    constructor() {
        this._articles = [];

        makeAutoObservable(this);
        this.loadArticles();
    }

    // Accesseur en lecture
    get articles() {
        return this._articles;
    }

    // Accesseur en écriture
    set articles(articles) {
        this._articles = articles;
    }

    // Méthodes
    async loadArticles() {
        const listArticles = await fetch("http://localhost:8000/wp-json/wc/v3/products").then(res => res.json());
        this._articles = listArticles.map(article => new Article(article.id, article.titre, article.disponibilite, article.image, article.keyword, article.prix, article.coupons, article.priceAfterCoupons));
     }


    getArticleById(id) {
        return this._articles.find(article => article.id === id);
    }

}

export default ArticleStore;
