import React, { useContext } from "react";
import { GlobalContext } from '../App.jsx';
import QuantityButton from "./QuantityButton";
import Bar from "./Bar.jsx";

// Définissez votre icône SVG dans une constante
const fingerIcon = (
    <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 200 200"
        className="w-6 h-6 text-primary-color absolute bottom-2 right-2"
    >
        <circle cx="89.43" cy="64.48" r="19.46" />
        <path
            d="M139.93,102.68,98.81,95.75V65.2A9.25,9.25,0,0,0,89.56,56h0a9.25,9.25,0,0,0-9.25,9.25v57.36L71,111.77c-3.61-3.61-8.44-3.89-13.08,0,0,0-7.24,5.84-3.83,9.25l34,34h42.63a9.25,9.25,0,0,0,9.07-7.43l6.82-34.09A9.28,9.28,0,0,0,139.93,102.68Z"
            className="text-primary-color"
        />
    </svg>
);

function Article({ article, popup = false }) {
    return (
        <article className="flex flex-col justify-between w-2/5 m-4 p-2 items-center ">
            <Bar width="w-full" height="h-2" className="w-full m-2" />
            <div className="flex justify-between h-8/12">
                <div className="flex flex-col w-10/12 h-full">
                    <div className="overflow-hidden relative">
                        {popup === false ? (
                            <img
                                className="w-full"
                                src={`../../pictures/${article.pictures[0]}`}
                                alt={article.alt}
                            />
                        ) : (
                            <figure className="slider flex">
                                {article.pictures.map((picture, index) => (
                                    <img
                                        className="float-left "
                                        src={`../../pictures/${picture}`}
                                        alt={article.alt}
                                        key={index}
                                    ></img>
                                ))}
                            </figure>
                        )}
                        {popup === false && (
                            // Intégrez votre icône SVG ici
                            <div className="absolute bottom-2 right-2">
                                {fingerIcon}
                            </div>
                        )}
                    </div>
                    <div className="flex justify-between mt-3 ">
                        <p className="text-2xl bg-primary-color text-light-color p-3 w-9/12">{article.name}</p>
                        <div className="flex justify-center items-center bg-light-color-dark shadow-inner-shadow w-2/12">
                            <p className="text-2xl ">{article.price}$</p>
                        </div>
                    </div>
                </div>
                <div className="h-full w-2/12 pl-4">
                    <QuantityButton className={"h-full shadow-outer-shadow bg-light-color-lightened rounded p-2"} row={false} productId={article.id} article={article} />
                </div>
            </div>
            <Bar width="w-full" height="h-2" className="w-full m-2" />
        </article>
    );
}

export default Article;
