import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCreditCard } from '@fortawesome/free-regular-svg-icons';
import { faMoneyBill1 } from '@fortawesome/free-regular-svg-icons';

function PaiementCard({icon, link, text, height = "h-full", width = "w-1/2"}) {

    const choosenIcon = icon === "faCreditCard" ? faCreditCard : '';
    const tedting = faCreditCard;

    if (icon === "faCreditCard") {
        const choosenIcon2 = faCreditCard;
    }
    

    return (
        <Link className={`flex flex-col ${!icon ? 'justify-center' : 'justify-between'} ${height} ${width} py-12 px-12 bg-light-color-lightened`} to={link}>
            {icon &&  <FontAwesomeIcon icon={tedting} className="w-full paiement-icon"/>}
            <h2 className="text-5xl italic text-center">{text}</h2>
            <FontAwesomeIcon icon={choosenIcon}/>
        </Link>
  
    )
}

export default PaiementCard;
