import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronRight } from '@fortawesome/free-solid-svg-icons';
import { faChevronLeft } from '@fortawesome/free-solid-svg-icons';

function NavItem({name, link, className, iconRight = true}) {

    const clearStorage = () => {
        cartStore.clearLocalStorage();
    };

    return (
        <li className={`py-2 px-5 text-xl bg-light-color-lightened border-custom shadow-button-shadow ${className}`}>
           {iconRight ===true ? <Link className='space-x-12'  to={link}><span>{name}</span> <FontAwesomeIcon icon={faChevronRight} /></Link>
                              : <Link className='space-x-12'  to={link}><FontAwesomeIcon icon={faChevronLeft} /><span>{name}</span></Link>
           }
        </li>
        

    )
}


export default NavItem;
