import React, { useContext, useState } from "react";
import { GlobalContext } from '../App.jsx';
import QuantityButton from "./QuantityButton";
import Bar from "./Bar.jsx";
import ArticleModal from "./ArticleModal.jsx";


function Article({ article, popup = false, bar = true }) {

    const [showModal, setShowModal] = useState(false);
    const [selectedArticle, setSelectedArticle] = useState(null);

    const openModal = (article) => {
        setSelectedArticle(article);
        setShowModal(true);
    };

    const closeModal = () => {
        setSelectedArticle(null);
        setShowModal(false);
    };

    return (
        <article className="flex flex-col justify-between w-2/5 m-4 p-2 items-center ">

            {bar === true ? <Bar width="w-full" height="h-2" className="w-full m-2" /> : ''}
            <div className="flex justify-between h-8/12">
            <div className="overflow-hidden relative">
        <img
          className="w-full"
          src={`../../pictures/${article.pictures[0]}`}
          onClick={() => openModal(article)} // Ouvrez la modal au clic sur l'image
        />
        <div className="absolute bottom-2 right-2 w-12 h-12 light-border flex justify-center items-center">
          <img src="../../index_picto.svg" alt="Cliquez ici" className="w-2/3 h-2/3 hand-tap" />
        </div>
      </div>
                <div className="h-full w-2/12 pl-2">
                    <QuantityButton className={"h-full shadow-outer-shadow bg-light-color-lightened rounded p-2"} row={false} article={article} />
                </div>
            </div>
            {bar === true ? <Bar width="w-full" height="h-2" className="w-full m-2" /> : ''}

            {showModal && selectedArticle && (
                <ArticleModal article={selectedArticle} onClose={closeModal} />
            )}
        </article>



    )
}

export default Article;
