import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link } from "react-router-dom";

function PaiementCard({icon, link, text, height = "h-full", width = "w-1/2"}) {

    return (
        <Link className={`flex flex-col ${!icon ? 'justify-center' : 'justify-between'} ${height} ${width} py-12 px-12 bg-light-color-lightened`} to={link}>
            {icon &&  <FontAwesomeIcon icon={`fa-regular ${icon}`} className="w-full " />}
            <h2 className="text-5xl italic text-center">{text}</h2>
        </Link>
  
    );
}
