import React, { useState, useContext } from "react";
import Button from "./Button";
import { GlobalContext } from '../App.jsx';
import { observer } from "mobx-react-lite";

function QuantityButton({ row = true, className, article, onQuantityChange }) {




    const direction = row ? "row" : "col";

    const handleIncrement = () => {
        onQuantityChange(article.id, article.quantite + 1);
      };
    
      const handleDecrement = () => {
        if (article.quantite > 0) {
          onQuantityChange(article.id, article.quantite - 1);
        }
      };

    return (

        <div className={`flex flex-${direction} justify-between items-center w-2/12 ${className && className}`}>
            {row === false ? <Button title="Ajouter" icon="faPlus" onClick={handleIncrement} /> : <Button title="Retirer" icon="faMinus" onClick={handleDecrement} />}
            <span>{article.quantite}</span>
            {row === false ? <Button title="Retirer" icon="faMinus" onClick={handleDecrement} /> : <Button title="Ajouter" icon="faPlus" onClick={handleIncrement} />}
        </div>

    )
}

export default QuantityButton;
