import React from 'react';

function Article({ idJson }) {

    //Importation des données des articles
    const { articlesData } = require('../../public/data.json');

    function findArticleById(id) {
        return articlesData.find(article => article.id === id);
      }

    const currentArticle = findArticleById(idJson);

    if (currentArticle) {
        const name = currentArticle.name;
        const price = currentArticle.price;
        const picture = currentArticle.quantity;
      } else {
        console.log(`Aucun article conrrespondant avec l'ID ${idJson}`);
      }

