import React, { useState } from "react";
import articlesData from '../data.json';


function Article({ article }) {

    const [quantity, setQuantity] = useState(0);

    return (
        <li>
            <div><td>{article.id}</td>
      <td><img src={article.picture} alt="Produit" className="w-10" /></td>
      <td>{article.title.substring(0, 50)}</td>
      <td className='text-center'>{article.disponibility}</td>
      <td>{article.keyword}</td>
      <td className="whitespace-nowrap">{article.price} €</td>
      <td className="whitespace-nowrap">{article.newPrice} {article.newPrice && "€"}</td>
      <td>
        { article.coupons.map((coupon,index) => (
          <span key={index}>{coupon}</span>
        ))}
      </td> </div>
            Quantité actuelle : {quantity}
            <button className="bg-sky-300" onClick={() => setQuantity(quantity + 1)}>
                Ajouter
            </button>
            <button className="bg-red-300" onClick={() => setQuantity(quantity + -1)}>
                Retirer
            </button>
        </li>



    )
}

export default Article;
