import {
    makeAutoObservable,
    runInAction
} from "mobx";
import Article from "./Article";

class ArticleStore {


    // Attributs
    _articles;

    // Constructeur
    constructor() {
        this._articles = [];

        makeAutoObservable(this);
        this.loadArticles();
    }

    // Accesseur en lecture
    get articles() {
        return this._articles;
    }

    // Accesseur en écriture
    set articles(articles) {
        this._articles = articles;
    }

    // Méthodes
   
  loadArticles() {
    // Chargez le fichier JSON
    fetch("../data.json")
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP ! Statut : ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        // Transformez les données en objets Article et stockez-les dans this._articles
        this._articles = data.map(article => new Article(
          article.id,
          article.name,
          article.description,
          article.prix,
          article.picture,
          article.quantity,
          article.alt,
        ));
        console.log(this._articles);
      })
      .catch(error => {
        console.error("Erreur lors du chargement des articles :", error);
      });
  }


    getArticleById(id) {
        return this._articles.find(article => article.id === id);
    }

}

export default ArticleStore;
