import Article from '../components/Article';
import React, { useContext,useState } from 'react';
import Nav from '../components/Nav'
import { GlobalContext } from '../App.jsx';
import { observer } from 'mobx-react-lite';
import Panier from '../components/Panier';

function Expositions() {

    const { articleStore } = useContext(GlobalContext);


    // On acccède aux données des articles via ArticleStore
    const articles = articleStore.articles;

    // Filtrer les articles qui correspondent aux expositions
    const filteredArticles = articles.filter(article => article.id.slice(0, 3) === 'EXP');

    const [articlesList, setArticles] = useState(articles);
    const [panier, setPanier] = useState([]);
  
    const handleQuantityChange = (articleId, newQuantity) => {
      const updatedArticles = articles.map((article) => {
        if (article.id === articleId) {
          return { ...article, quantite: newQuantity };
        }
        return article;
      });
  
      setArticles(updatedArticles);

      const existingItem = panier.find((item) => item.article.id === articleId);

      if (existingItem) {
        const updatedPanier = panier.map((item) => {
          if (item.article.id === articleId) {
            return { ...item, quantity: newQuantity };
          }
          return item;
        });
        setPanier(updatedPanier);
      } else {
        const articleToAdd = articles.find((article) => article.id === articleId);
        setPanier([...panier, { article: articleToAdd, quantity: newQuantity }]);
      }
    };
  

    return (
        <>
            <Nav />
            <main>
                <h1>Expositions</h1>
                <div className='flex flex-wrap justify-center'>
                    {filteredArticles.map(article => (
                        <Article key={article.id} article={article} />
                    ))}
                </div>
                <Panier />

            </main>
        </>
    )
}
export default observer(Expositions);
